package com.lindroy.networkrequestpractice.logic.network.base.repositoryimport androidx.lifecycle.LiveDataimport androidx.lifecycle.liveDataimport com.lindroy.networkrequestpractice.logic.model.*import com.lindroy.networkrequestpractice.logic.network.base.RequestExceptionimport com.lindroy.networkrequestpractice.logic.network.base.BaseResponseimport com.lindroy.networkrequestpractice.logic.network.handleExceptionimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.coroutineScopeimport kotlinx.coroutines.withContextimport kotlin.coroutines.CoroutineContext/** * @author Lin * @date 2021/10/15 * @function 唯一数据源 */abstract class BaseRepository {    protected suspend fun <T> fire(        block: suspend () -> BaseResponse<T>    ): BaseResponse<T> = withContext(Dispatchers.IO) {        var response: BaseResponse<T> = EmptyResponse()        block.invoke()        kotlin.runCatching {            block.invoke()        }.onSuccess {            response = when (it.success) {                true -> checkEmptyResponse(it.data)                false -> FailureResponse(handleException(RequestException(it)))            }        }.onFailure { throwable ->            response = FailureResponse(handleException(throwable))        }        response    }    /**     * data 为 null，或者 data 是集合类型，但是集合为空都会进入 onEmpty 回调     */    private fun <T> checkEmptyResponse(data: T?): ApiResponse<T> =        if (data == null || (data is List<*> && (data as List<*>).isEmpty())) {            EmptyResponse()        } else {            SuccessResponse(data)        }}